mapper.select:
select lineId, openId, unionId, province, city, location, A.storeId, storeName, longitude, latitude, limitNumber, createTime, updateTime,area,posName, posid from tSMGStoreLocation A LEFT JOIN tSMGPosConfiguration B ON A.storeId=B.storeId where 1=1
AND lineId = #{lineId}
ORDER BY lineId ASC
